export const POST = async (req: Request) => {
  const body = (await req.json()) as HeroSkillUpdatePayload;
  const { HeroSkills } = body;

  //Generated by GenerateUniquenessValidation
  //Validate record uniqueness

  UNIQUE_FIELDS.forEach((item) => {
    const uniquenessError = checkDuplicateCombinations(item, HeroSkills);

    if (uniquenessError) {
      return returnJSONResponse({
        status: "error",
        error: uniquenessError,
        errorCode: 422,
      });
    }
  });

  const t = await sequelize.transaction();
  let recordsCreated = 0;

  for (const item of HeroSkills) {
    try {
      await HeroSkillSchema.validate(item);
    } catch (error) {
      return handleSequelizeError(error);
    }
  }

  try {
    for (const item of HeroSkills) {
      if (item[PRIMARY_KEY] === "") {
        await createHeroSkill(item, t);
        recordsCreated++;
      } else {
        await updateHeroSkill(item, PRIMARY_KEY, t);
      }
    }

    await t.commit();

    return NextResponse.json({
      recordsCreated,
    });
  } catch (err) {
    await t.rollback();
    return handleSequelizeError(err);
  }
};

export const DELETE = async (req: Request) => {
  const body = (await req.json()) as HeroSkillDeletePayload;
  const { deletedHeroSkills } = body;

  if (deletedHeroSkills.length > 0) {
    const t = await sequelize.transaction();
    try {
      await deleteHeroSkills(PRIMARY_KEY, deletedHeroSkills, t);
      t.commit();
      return NextResponse.json("success");
    } catch (error) {
      t.rollback();
      return handleSequelizeError(error);
    }
  }
};
