"use client";
import { GetHeroesResponse } from "@/interfaces/HeroInterfaces";
import { HeroSearchParams } from "@/interfaces/HeroInterfaces";
import { BasicModel } from "@/interfaces/GeneralInterfaces";
import axiosClient from "@/utils/api";
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";

const getHeroes = async () => {
  const { data } = await axiosClient.get<GetHeroesResponse>(`heroes`, {
    params: {
      fetchCount: "false",
      simpleOnly: "true",
    } as Partial<HeroSearchParams>,
  });

  return data.rows.map((item) => ({
    id: item.belongsto_id,
    name: item.heroName,
  }));
};

interface UseListProps {
  placeholderData?: BasicModel[];
}

const useHeroList = (prop?: UseListProps) => {
  //local states
  const [mounted, setMounted] = useState(false);

  const _ = useQuery({
    queryKey: ["hero-list"],
    queryFn: getHeroes,
    enabled: mounted,
    placeholderData: prop?.placeholderData,
  });

  useEffect(() => {
    setMounted(true);
  }, []);

  return _;
};

export default useHeroList;
